import { Component } from '@angular/core';

@Component({
  selector: 'app-ingredient',
  templateUrl: './ingredient.component.html',
  styleUrls: ['./ingredient.component.css']
})
export class IngredientComponent {
    ingredientName = ""
    ingredientPrice = 0
    ingredientType = ""

    getIngredientName(){
      return this.ingredientName; 
    }
}
