import { Component } from '@angular/core';
import { Recipe } from '../receipe/receipe.model';

@Component({
  selector: 'app-recipe-book',
  templateUrl: './recipe-book.component.html',
  styleUrls: ['./recipe-book.component.css'],
})
export class RecipeBookComponent {
  recipes: Recipe[] = [
    new Recipe(
      'Chicken Biryani',
      'A Hyderabadi Dish',
      'https://glebekitchen.com/wp-content/uploads/2019/12/chickenbiryanibowltop.jpg'
    ),
  ];
  getImagepath() {
    return this.recipes[0].imagePath;
  }
}
<div class="row">
  <div class="col-xs-12">
    <button class="btn btn-success">New Recipe</button>
  </div>
</div>
<div class="row">
  <div class="col-xs-12">
    <a href="#" class="list-group-item "  *ngFor="let recipe of recipes">
      <div class="pull-left">
        <h4 class="list-group-item-heading">{{ recipe.name }}</h4>
        <p class="list-group-item">{{ recipe.description }}</p>
      </div>
      <span class="pull-right">
        <img
          [src]="recipe.imagePath"
          alt=""
          class="img-responsive"
          style="max-height: 50px"
        />
      </span>
    </a>
    <app-recipe-item></app-recipe-item>
  </div>
</div>
