import { Component } from '@angular/core';

@Component({
  selector: 'app-ingredient',
  templateUrl: './ingredient.component.html',
  styleUrls: ['./ingredient.component.css'],
})
export class IngredientComponent {
  ingredientName = '';
  ingredientPrice = 0;
  ingredientType = '';
  showtext = false

  onNewIngredient() {
    let text =
      'The Ingredient  '+ this.ingredientName+' Of type '+this.ingredientType +' is '+this.ingredientPrice+' only';
      this.showtext = true
  }
}
<h1>Add Ingredient Form</h1>
<label for="">Ingredient Name: </label>
<input type="text" [(ngModel)]="ingredientName"> <br><br>
<label for="">Ingredient Type: </label>
<input type="text" [(ngModel)]="ingredientType"> <br> <br>
<label for="">Ingredient Price: </label>
<input type="number" [(ngModel)]="ingredientPrice"> <br> <br>

<button>Show New Ingredient</button>
