import { Component } from '@angular/core';

@Component({
  selector: 'app-ingredient',
  templateUrl: './ingredient.component.html',
  styleUrls: ['./ingredient.component.css'],
})
export class IngredientComponent {
  ingredientName = '';
  ingredientPrice = 0;
  ingredientType = '';
  showtext = false

  onNewIngredient() {
    let text =
      'The Ingredient  '+ this.ingredientName+' Of type '+this.ingredientType +' is '+this.ingredientPrice+' only';
      this.showtext = true
  }
}
